    .example-wrapper { margin: 1em auto; max-width: 800px; width: 95%; font: 18px/1.5 sans-serif; }
    .example-wrapper code { background: #F5F5F5; padding: 2px 6px; }
</style>

<div class=\"container\">
    <h1>All Products</h1>
    <div class=\"row\">
    {% for p in products %}
        <div class=\"col-4\">
            <div class=\"card\">
                <a href=\"/product/{{ p.id }}\"> <img src=\"https://tn.jumia.is/unsafe/fit-in/300x300/filters:fill(white)/product/95/4203/1.jpg?4700\" alt=\"iphone\"></img> </a>
            
            <div class=\"card-body\">
                <span class=\"bold\">{{ p.name }}</span>
                <br>
                <span class=\"bold\">{{ p.price }} TND</span>
            </div>
            </div>
        </div>
    {% endfor %}

    </div>

    </div>
{% endblock %}
